import React, { useState, useRef, useEffect } from 'react';
import '../index.css';
import './VulnerabilityPage.css';

/*
  Authentication Sandbox (enhanced)
  - Dynamic in-memory user store (register users)
  - Manual login attempts always append to attack log
  - Async credential-stuffing simulator with start/stop
  - Console-style attack log with auto-scroll and timestamps
  - All styling uses existing vp-* classes (VulnerabilityPage.css)
*/

const initialUsers = [
  { username: 'alice', password: 'Password@123' },
  { username: 'bob', password: 'qwerty' }, // intentionally weak
  { username: 'admin', password: 'admin123' } // intentionally default
];

export default function AuthenticationSandbox() {
  const [users, setUsers] = useState(initialUsers);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('Try logging in. Hint: bob / qwerty or admin / admin123');
  const [attempts, setAttempts] = useState(0);
  const [attackLog, setAttackLog] = useState([]); // each entry: {ts, text}
  const [isLocked, setIsLocked] = useState(false);

  // Simulation state
  const [isSimulating, setIsSimulating] = useState(false);
  const simCancelRef = useRef(false);

  // Console scroll
  const consoleEndRef = useRef(null);

  // helper to push a timestamped log entry
  function pushLog(text) {
    const ts = new Date().toLocaleTimeString();
    setAttackLog(prev => [...prev, { ts, text }]);
  }

  // auto-scroll to bottom when attackLog changes
  useEffect(() => {
    if (consoleEndRef.current) {
      // small timeout to ensure element rendered
      setTimeout(() => {
        consoleEndRef.current.scrollIntoView({ behavior: 'smooth', block: 'end' });
      }, 30);
    }
  }, [attackLog]);

  // Manual login handler (always appends to attack log; works after simulation)
  function handleLogin(e) {
    e?.preventDefault?.();
    if (isLocked) {
      setMessage('Account is temporarily locked due to too many attempts (simulated).');
      pushLog(`Attempted login for ${username} while account locked`);
      return;
    }

    setAttempts(a => a + 1);
    const found = users.find(u => u.username === username && u.password === password);

    if (found) {
      setMessage(`âœ… Welcome, ${username}! (Simulated login)`);
      pushLog(`Successful login: ${username}`);
    } else {
      setMessage('âŒ Invalid username or password.');
      pushLog(`Failed login attempt: ${username}`);
    }

    // simulated lockout threshold (5) â€” if reached, mark locked
    if (attempts + 1 >= 5) {
      setIsLocked(true);
      setMessage('ðŸ”’ Too many failed attempts â€” account locked (simulated).');
      pushLog(`Account lockout triggered (simulated) after ${attempts + 1} failures`);
    }
  }

  // Allow registering new users dynamically
  function handleRegister(e) {
    e?.preventDefault?.();
    if (!username || !password) {
      setMessage('Enter username and password to register.');
      return;
    }
    if (users.some(u => u.username === username)) {
      setMessage('User already exists. Try another username.');
      pushLog(`Registration failed: ${username} already exists`);
      return;
    }
    // add user to in-memory list
    setUsers(prev => [...prev, { username, password }]);
    setMessage(`âœ… Registered ${username} (in-memory).`);
    pushLog(`Registered new user: ${username}`);
    // keep fields for quick login testing
  }

  // Reset demo state (does not delete registered users; resets attempts/log)
  function resetDemo() {
    setUsername('');
    setPassword('');
    setMessage('Try logging in. Hint: bob / qwerty or admin / admin123');
    setAttempts(0);
    setAttackLog([]);
    setIsLocked(false);
    setIsSimulating(false);
    simCancelRef.current = false;
    pushLog('Demo reset');
  }

  // credential-stuffing simulation (async, cancellable)
  async function runCredentialStuffingAsync({ delayMs = 300 } = {}) {
    // small lists for demo
    const common = ['123456', 'password', 'qwerty', 'admin', 'admin123'];
    // Use current users' usernames + some common targets
    const targets = Array.from(new Set([...users.map(u => u.username), 'carol', 'dave']));

    setIsSimulating(true);
    simCancelRef.current = false;
    pushLog('--- Credential Stuffing Simulation started ---');

    for (let t of targets) {
      if (simCancelRef.current) break;
      for (let pw of common) {
        if (simCancelRef.current) break;

        // emulate network delay
        await new Promise(r => setTimeout(r, delayMs));

        const success = users.some(u => u.username === t && u.password === pw);
        const text = `${t}:${pw} -> ${success ? 'SUCCESS' : 'FAIL'}`;
        pushLog(text);
      }
    }

    if (simCancelRef.current) {
      pushLog('--- Credential Stuffing Simulation stopped by user ---');
    } else {
      pushLog('--- Credential Stuffing Simulation completed ---');
    }
    setIsSimulating(false);
    simCancelRef.current = false;
  }

  function startSimulation() {
    if (isSimulating) return;
    runCredentialStuffingAsync({ delayMs: 250 }).catch(err => {
      pushLog(`Simulation error: ${String(err)}`);
      setIsSimulating(false);
      simCancelRef.current = false;
    });
  }

  function stopSimulation() {
    if (!isSimulating) return;
    simCancelRef.current = true;
    setIsSimulating(false); // UI flag; actual async loop checks simCancelRef
  }

  // small helper to display masked users (for teaching)
  function maskedUsers() {
    return users.map(u => ({
      username: u.username,
      password: u.password.length > 2 ? u.password[0] + '*'.repeat(Math.max(0, u.password.length - 2)) + u.password.slice(-1) : u.password
    }));
  }

  return (
    <div className="vp-page vp-auth-sandbox">
      <section className="vp-hero">
        <div className="vp-hero-content">
          <div className="vp-hero-text">
            <div className="vp-badge">A02:2021</div>
            <h1 className="vp-title">Authentication Failures â€” Sandbox (Enhanced)</h1>
            <p className="vp-subtitle">
              Interactive demo with dynamic users and a cancellable credential-stuffing simulator.
              Everything runs in-browser for safe learning.
            </p>
          </div>
        </div>
      </section>

      <main className="vp-container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'flex-start' }}>
        <div className="vp-grid" style={{ maxWidth: 1000, margin: '0 auto', gap: 16 }}>
          <div className="vp-card" style={{ textAlign: 'center' }}>
            <h3>Login / Register</h3>

            <form onSubmit={handleLogin} className="vp-form" style={{ marginTop: 8 }}>
              <label>Username</label>
              <input value={username} onChange={e => setUsername(e.target.value)} placeholder="username" className="vp-input" />
              <label>Password</label>
              <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="password" className="vp-input" />
              <div style={{ marginTop: 12 }}>
                <button className="vp-btn" type="submit">Login</button>
                <button type="button" className="vp-btn vp-btn-secondary" onClick={handleRegister} style={{ marginLeft: 8 }}>
                  Register (in-memory)
                </button>
                <button type="button" className="vp-btn vp-btn-secondary" onClick={resetDemo} style={{ marginLeft: 8 }}>
                  Reset Demo
                </button>
              </div>
            </form>

            <p className="vp-note" style={{ marginTop: 10 }}>{message}</p>

            <div style={{ marginTop: 12, display: 'flex', justifyContent: 'center', gap: 8 }}>
              {!isSimulating ? (
                <button className="vp-btn vp-btn-danger" onClick={startSimulation}>Start Credential Stuffing</button>
              ) : (
                <button className="vp-btn vp-btn-secondary" onClick={stopSimulation}>Stop Simulation</button>
              )}
            </div>

            <div style={{ marginTop: 12 }}>
              <h4>Registered Users (masked)</h4>
              <div style={{ display: 'flex', justifyContent: 'center', gap: 12, flexWrap: 'wrap', marginTop: 8 }}>
                {maskedUsers().map(u => (
                  <div key={u.username} style={{ background: 'rgba(255,255,255,0.02)', padding: '6px 10px', borderRadius: 8 }}>
                    <strong>{u.username}</strong><div style={{ fontSize: 12 }}>{u.password}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          <div className="vp-card" style={{ textAlign: 'left', display: 'flex', flexDirection: 'column' }}>
            <h3 style={{ marginBottom: 8 }}>Attack Log â€” Console</h3>

            <div
              className="vp-log"
              style={{
                background: '#0b1020',
                color: '#d1d5db',
                fontFamily: 'ui-monospace, SFMono-Regular, Menlo, Monaco, "Roboto Mono", monospace',
                padding: '12px',
                borderRadius: 8,
                minHeight: 260,
                maxHeight: 420,
                overflowY: 'auto',
                border: '1px solid rgba(255,255,255,0.04)'
              }}
            >
              {attackLog.length === 0 ? (
                <div style={{ opacity: 0.7 }}>Console empty â€” perform logins or start the simulation.</div>
              ) : (
                attackLog.map((entry, i) => (
                  <div key={i} style={{ marginBottom: 6 }}>
                    <span style={{ color: '#9ca3af' }}>[{entry.ts}] </span>
                    <span>{entry.text}</span>
                  </div>
                ))
              )}

              {/* sentinel element for auto-scroll */}
              <div ref={consoleEndRef} />
            </div>

            <div style={{ marginTop: 12 }}>
              <p style={{ margin: 0 }}>
                <strong>Notes:</strong> Simulation runs asynchronously and can be stopped at any time.
                Manual login attempts always append to this console. The demo is ephemeral and runs only in your browser.
              </p>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
