import React, { useState, useRef, useEffect } from 'react';
import '../index.css';
import './VulnerabilityPage.css';

/*
  Authentication Sandbox (persistent + replay + attempt counters)
  - Dynamic in-memory user store persisted to localStorage
  - Remaining-attempts UI per user
  - Manual login/register affects persisted store and attack log
  - Async credential-stuffing simulator (start/stop) that respects lockouts
  - Attack log persists; "Replay" feature replays entries step-by-step
  - Console-style attack log with auto-scroll and timestamps
*/

const LOCAL_USERS_KEY = 'xploitsim_users_v1';
const LOCAL_LOG_KEY = 'xploitsim_attacklog_v1';

const DEFAULT_USERS = [
  { username: 'alice', password: 'Password@123', attempts: 0, lockedUntil: 0 },
  { username: 'bob', password: 'qwerty', attempts: 0, lockedUntil: 0 },
  { username: 'admin', password: 'admin123', attempts: 0, lockedUntil: 0 }
];

const MAX_ATTEMPTS = 5;
const LOCK_DURATION_MS = 60 * 1000; // 60 seconds lockout for demo

export default function AuthenticationSandbox() {
  // persisted users & logs
  const [users, setUsers] = useState(() => {
    try {
      const raw = localStorage.getItem(LOCAL_USERS_KEY);
      if (raw) {
        const parsed = JSON.parse(raw);
        // ensure schema (old entries get defaults)
        return parsed.map(u => ({ attempts: 0, lockedUntil: 0, ...u }));
      }
    } catch (e) {
      // ignore parse error
    }
    // fallback
    return DEFAULT_USERS.slice();
  });

  const [attackLog, setAttackLog] = useState(() => {
    try {
      const raw = localStorage.getItem(LOCAL_LOG_KEY);
      if (raw) return JSON.parse(raw);
    } catch (e) {}
    return []; // each entry: { ts, text }
  });

  // form + UI state
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('Try logging in. Hint: bob / qwerty or admin / admin123');
  const [isSimulating, setIsSimulating] = useState(false);
  const simCancelRef = useRef(false);
  const consoleEndRef = useRef(null);
  const consoleRef = useRef(null); // <-- new: ref to the scrollable console container


  // Replay state
  const [replayIndex, setReplayIndex] = useState(0);
  const [isReplaying, setIsReplaying] = useState(false);
  const replayTimerRef = useRef(null);
  const REPLAY_INTERVAL_MS = 700;

  // persist users & logs when changed
  useEffect(() => {
    try {
      localStorage.setItem(LOCAL_USERS_KEY, JSON.stringify(users));
    } catch (e) {}
  }, [users]);

  useEffect(() => {
    try {
      localStorage.setItem(LOCAL_LOG_KEY, JSON.stringify(attackLog));
    } catch (e) {}
  }, [attackLog]);

  // Auto-scroll console on log update
  useEffect(() => {
    // scroll inner console container to its bottom (no page jump)
    const el = consoleRef.current;
    if (!el) return;
    // set immediate scroll so large bursts don't cause page relocation
    // use requestAnimationFrame to ensure DOM updated
    requestAnimationFrame(() => {
      try {
        el.scrollTop = el.scrollHeight;
      } catch (e) {
        // fallback: no-op
      }
    });
  }, [attackLog]);


  // helper for lock checking & cleaning expired locks
  function isCurrentlyLocked(user) {
    if (!user || !user.lockedUntil) return false;
    const now = Date.now();
    if (user.lockedUntil > now) return true;
    // expired => clear lock (reset attempts too)
    updateUser(user.username, { lockedUntil: 0, attempts: 0 });
    return false;
  }

  // push a timestamped log entry
  function pushLog(text) {
    const ts = new Date().toLocaleTimeString();
    setAttackLog(prev => {
      const next = [...prev, { ts, text }];
      return next;
    });
  }

  // update a single user's fields by username
  function updateUser(uname, fields) {
    setUsers(prev => prev.map(u => (u.username === uname ? { ...u, ...fields } : u)));
  }

  // add user (register)
  function addUser(uname, pwd) {
    setUsers(prev => {
      if (prev.some(u => u.username === uname)) return prev;
      return [...prev, { username: uname, password: pwd, attempts: 0, lockedUntil: 0 }];
    });
  }

  // masked user view for UI (remaining attempts, locked flag)
  function maskedUsers() {
    return users.map(u => {
      const pw = u.password || '';
      const masked = pw.length <= 2 ? pw : pw[0] + '*'.repeat(Math.max(0, pw.length - 2)) + pw.slice(-1);
      const attempts = u.attempts || 0;
      const remaining = Math.max(0, MAX_ATTEMPTS - attempts);
      const lockedNow = (u.lockedUntil || 0) > Date.now();
      return { username: u.username, masked, remaining, lockedNow, attempts: attempts, lockedUntil: u.lockedUntil || 0 };
    });
  }

  // Manual login handler
  function handleLogin(e) {
    e?.preventDefault?.();
    const uname = username.trim();
    if (!uname) {
      setMessage('Enter a username.');
      return;
    }
    const user = users.find(u => u.username === uname);

    if (user && isCurrentlyLocked(user)) {
      setMessage('ðŸ”’ Account locked (simulated).');
      pushLog(`Attempted login for ${uname} while locked`);
      return;
    }

    if (!user) {
      setMessage('âŒ Unknown user.');
      pushLog(`Failed login attempt: ${uname} (unknown user)`);
      return;
    }

    if (user.password === password) {
      setMessage(`âœ… Welcome, ${uname}! (Simulated login)`);
      pushLog(`Successful login: ${uname}`);
      // reset attempts on success
      updateUser(uname, { attempts: 0, lockedUntil: 0 });
    } else {
      // failure: increment attempts
      const newAttempts = (user.attempts || 0) + 1;
      if (newAttempts >= MAX_ATTEMPTS) {
        const until = Date.now() + LOCK_DURATION_MS;
        updateUser(uname, { attempts: newAttempts, lockedUntil: until });
        setMessage('ðŸ”’ Too many failed attempts â€” account locked (simulated).');
        pushLog(`Failed login attempt: ${uname} (attempt ${newAttempts})`);
        pushLog(`Account ${uname} locked until ${new Date(until).toLocaleTimeString()}`);
      } else {
        updateUser(uname, { attempts: newAttempts });
        setMessage('âŒ Invalid username or password.');
        pushLog(`Failed login attempt: ${uname} (attempt ${newAttempts})`);
      }
    }
  }

  // Register (in-memory)
  function handleRegister(e) {
    e?.preventDefault?.();
    const uname = username.trim();
    if (!uname || !password) {
      setMessage('Enter username and password to register.');
      return;
    }
    if (users.some(u => u.username === uname)) {
      setMessage('User already exists. Try another username.');
      pushLog(`Registration failed: ${uname} already exists`);
      return;
    }
    addUser(uname, password);
    setMessage(`âœ… Registered ${uname} (in-memory).`);
    pushLog(`Registered new user: ${uname}`);
  }

  // Reset demo - clears attempts & logs but keeps registered users
  function resetDemo() {
    setUsers(prev => prev.map(u => ({ ...u, attempts: 0, lockedUntil: 0 })));
    setAttackLog([]);
    setMessage('Demo reset. Users retained (registered).');
    setIsSimulating(false);
    simCancelRef.current = false;
    setReplayIndex(0);
    setIsReplaying(false);
    if (replayTimerRef.current) {
      clearInterval(replayTimerRef.current);
      replayTimerRef.current = null;
    }
    pushLog('Demo reset (attempts and log cleared).');
  }

  // Async credential stuffing simulation: respects lockouts and updates per-user attempts & locks
  async function runCredentialStuffingAsync({ delayMs = 300 } = {}) {
    const common = ['123456', 'password', 'qwerty', 'admin', 'admin123'];
    // targets: current usernames plus a couple of extras
    const targets = Array.from(new Set([...users.map(u => u.username), 'carol', 'dave']));

    setIsSimulating(true);
    simCancelRef.current = false;
    pushLog('--- Credential Stuffing Simulation started ---');

    for (const t of targets) {
      if (simCancelRef.current) break;

      // fetch fresh user snapshot from state
      const freshUser = users.find(u => u.username === t);

      // If user exists and locked, log as locked and skip tries for this user
      if (freshUser && isCurrentlyLocked(freshUser)) {
        pushLog(`${t}: <LOCKED> (simulation respects lockout)`);
        continue;
      }

      for (const pw of common) {
        if (simCancelRef.current) break;

        await new Promise(r => setTimeout(r, delayMs));

        // refetch user in case manual actions changed the store
        const u = users.find(x => x.username === t);

        if (!u) {
          pushLog(`${t}:${pw} -> FAIL (no such user)`);
          continue;
        }

        if (isCurrentlyLocked(u)) {
          pushLog(`${t}:${pw} -> FAIL (LOCKED)`);
          continue;
        }

        if (u.password === pw) {
          // success: reset attempts and log success
          updateUser(u.username, { attempts: 0, lockedUntil: 0 });
          pushLog(`${t}:${pw} -> SUCCESS`);
        } else {
          // failed attempt: increment attempts & maybe lock
          const newAttempts = (u.attempts || 0) + 1;
          if (newAttempts >= MAX_ATTEMPTS) {
            const until = Date.now() + LOCK_DURATION_MS;
            updateUser(u.username, { attempts: newAttempts, lockedUntil: until });
            pushLog(`${t}:${pw} -> FAIL (attempt ${newAttempts})`);
            pushLog(`${t} locked until ${new Date(until).toLocaleTimeString()}`);
          } else {
            updateUser(u.username, { attempts: newAttempts });
            pushLog(`${t}:${pw} -> FAIL (attempt ${newAttempts})`);
          }
        }
      }
    }

    if (simCancelRef.current) {
      pushLog('--- Credential Stuffing Simulation stopped by user ---');
    } else {
      pushLog('--- Credential Stuffing Simulation completed ---');
    }
    setIsSimulating(false);
    simCancelRef.current = false;
  }

  function startSimulation() {
    if (isSimulating) return;
    // ensure we use fresh users state inside async loop by referencing state variable inside loop â€” okay because we re-read users on each iteration
    runCredentialStuffingAsync({ delayMs: 240 }).catch(err => {
      pushLog(`Simulation error: ${String(err)}`);
      setIsSimulating(false);
      simCancelRef.current = false;
    });
  }

  function stopSimulation() {
    if (!isSimulating) return;
    simCancelRef.current = true;
    setIsSimulating(false);
  }

  // Replay controls
  function playReplay() {
    if (isReplaying || attackLog.length === 0) return;
    setIsReplaying(true);
    setReplayIndex(0);
    replayTimerRef.current = setInterval(() => {
      setReplayIndex(prev => {
        const next = prev + 1;
        if (next >= attackLog.length) {
          clearInterval(replayTimerRef.current);
          replayTimerRef.current = null;
          setIsReplaying(false);
          return attackLog.length;
        }
        return next;
      });
    }, REPLAY_INTERVAL_MS);
  }

  function pauseReplay() {
    if (replayTimerRef.current) {
      clearInterval(replayTimerRef.current);
      replayTimerRef.current = null;
    }
    setIsReplaying(false);
  }

  function stepReplay() {
    setReplayIndex(prev => Math.min(attackLog.length, prev + 1));
  }

  function resetReplay() {
    pauseReplay();
    setReplayIndex(0);
  }

  // render a "replay console" that shows entries up to replayIndex
  function renderReplayConsole() {
    if (attackLog.length === 0) {
      return <div style={{ opacity: 0.7 }}>No entries to replay.</div>;
    }
    const shown = attackLog.slice(0, replayIndex);
    return (
      <div style={{ fontFamily: 'ui-monospace, Menlo, Monaco, monospace' }}>
        {shown.length === 0 ? <div style={{ opacity: 0.7 }}>Replay paused (no entries shown yet).</div> :
          shown.map((e, i) => (<div key={i}><span style={{ color: '#9ca3af' }}>[{e.ts}] </span><span>{e.text}</span></div>))
        }
      </div>
    );
  }

  // small helper to clear persisted state entirely (dangerous) - used optionally in dev
  function clearAllStorageAndReset() {
    localStorage.removeItem(LOCAL_USERS_KEY);
    localStorage.removeItem(LOCAL_LOG_KEY);
    setUsers(DEFAULT_USERS.map(u => ({ ...u, attempts: 0, lockedUntil: 0 })));
    setAttackLog([]);
    setMessage('Cleared stored data; reset to defaults.');
    setReplayIndex(0);
  }

  return (
    <div className="vp-page vp-auth-sandbox">
      <section className="vp-hero">
        <div className="vp-hero-content">
          <div className="vp-hero-text">
            <div className="vp-badge">A02:2021</div>
            <h1 className="vp-title">Authentication Failures â€” Sandbox (Persistent)</h1>
            <p className="vp-subtitle">Dynamic users, persisted store, lockout-aware simulator, and replay controls. Runs in-browser only.</p>
          </div>
        </div>
      </section>

      <main className="vp-container" style={{ display: 'flex', justifyContent: 'center', alignItems: 'flex-start' }}>
        <div className="vp-grid" style={{ maxWidth: 1100, margin: '0 auto', gap: 16 }}>

          {/* LEFT: controls */}
          <div className="vp-card" style={{ textAlign: 'center' }}>
            <h3>Login / Register</h3>
            <form onSubmit={handleLogin} className="vp-form" style={{ marginTop: 8 }}>
              <label>Username</label>
              <input value={username} onChange={e => setUsername(e.target.value)} placeholder="username" className="vp-input" />
              <label>Password</label>
              <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="password" className="vp-input" />
              <div style={{ marginTop: 12 }}>
                <button className="vp-btn" type="submit">Login</button>
                <button
                  type="button"
                  className="vp-btn vp-btn-secondary"
                  onClick={handleRegister}
                  style={{
                    marginLeft: 8,
                    backgroundColor: '#444',
                    color: 'white',
                    border: '1px solid #777',
                  }}
                >
                  Register (in-memory)
                </button>

                <button
                  type="button"
                  className="vp-btn vp-btn-secondary"
                  onClick={resetDemo}
                  style={{
                    marginLeft: 8,
                    backgroundColor: '#444',
                    color: 'white',
                    border: '1px solid #777',
                  }}
                >
                  Reset Demo
                </button>
              </div>
            </form>

            <p className="vp-note" style={{ marginTop: 10 }}>{message}</p>

            <div style={{ marginTop: 12, display: 'flex', justifyContent: 'center', gap: 8 }}>
              {!isSimulating ? (
                <button className="vp-btn vp-btn-danger" onClick={startSimulation}>Start Credential Stuffing</button>
              ) : (
                <button className="vp-btn vp-btn-secondary" onClick={stopSimulation}>Stop Simulation</button>
              )}
              <button className="vp-btn vp-btn-secondary" onClick={() => { pushLog('Manual log entry added'); }}>Ping Log</button>
            </div>

            <div style={{ marginTop: 16 }}>
              <h4>Registered Users</h4>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: 10, justifyContent: 'center', marginTop: 8 }}>
                {maskedUsers().map(u => (
                  <div key={u.username} style={{ background: 'rgba(255,255,255,0.02)', padding: '8px 12px', borderRadius: 10, minWidth: 110 }}>
                    <div style={{ fontWeight: 700 }}>{u.username}{u.lockedNow ? ' ðŸ”’' : ''}</div>
                    <div style={{ fontSize: 12, opacity: 0.9 }}>{u.masked}</div>
                    <div style={{ marginTop: 6, fontSize: 13 }}>
                      Attempts: <strong>{u.attempts}</strong>
                      <div style={{ fontSize: 12, opacity: 0.8 }}>
                        Remaining: <strong>{u.remaining}</strong>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div style={{ marginTop: 10 }}>
              <button className="vp-btn vp-btn-secondary" onClick={clearAllStorageAndReset}>Clear Stored Data (dev)</button>
            </div>
          </div>

          {/* RIGHT: log + replay console */}
          <div className="vp-card" style={{ display: 'flex', flexDirection: 'column', textAlign: 'left' }}>
            <h3 style={{ marginBottom: 8 }}>Attack Log â€” Console</h3>

            <div className="vp-log" style={{
              background: '#0b1020',
              color: '#d1d5db',
              fontFamily: 'ui-monospace, Menlo, Monaco, monospace',
              padding: 12,
              borderRadius: 8,
              minHeight: 260,
              maxHeight: 360,
              overflowY: 'auto',
              border: '1px solid rgba(255,255,255,0.04)'
            }}>
              {attackLog.length === 0 ? (
                <div style={{ opacity: 0.7 }}>Console empty â€” perform logins or start the simulation.</div>
              ) : (
                attackLog.map((entry, i) => (
                  <div key={i} style={{ marginBottom: 6 }}>
                    <span style={{ color: '#9ca3af' }}>[{entry.ts}] </span>
                    <span>{entry.text}</span>
                  </div>
                ))
              )}
              <div ref={consoleEndRef} />
            </div>

            <div style={{ marginTop: 12, display: 'flex', gap: 8, alignItems: 'center' }}>
              <button className="vp-btn vp-btn-secondary" onClick={() => { setAttackLog([]); setMessage('Log cleared'); pushLog('Attack log cleared'); }}>Clear Log</button>

              {/* Replay controls */}
              <div style={{ marginLeft: 'auto', display: 'flex', gap: 8, alignItems: 'center' }}>
                <button className="vp-btn" onClick={playReplay} disabled={attackLog.length === 0 || isReplaying}>Play Replay</button>
                <button className="vp-btn vp-btn-secondary" onClick={pauseReplay} disabled={!isReplaying}>Pause</button>
                <button className="vp-btn vp-btn-secondary" onClick={stepReplay}>Step</button>
                <button className="vp-btn vp-btn-secondary" onClick={resetReplay}>Reset Replay</button>
              </div>
            </div>

            <div style={{ marginTop: 12 }}>
              <h4>Replay Console</h4>
              <div style={{
                background: '#02040a',
                color: '#cbd5e1',
                padding: 10,
                borderRadius: 8,
                minHeight: 120,
                fontFamily: 'ui-monospace, Menlo, Monaco, monospace',
                overflowY: 'auto'
              }}>
                {renderReplayConsole()}
              </div>
            </div>
          </div>

        </div>
      </main>
    </div>
  );
}
