import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function Navbar() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const closeSidebar = () => {
    setIsSidebarOpen(false);
  };

  const handleLinkClick = (anchor) => {
    // If we're not on the home page, navigate to home first
    if (window.location.pathname !== '/') {
      navigate('/');
      // Wait for navigation then scroll
      setTimeout(() => {
        const element = document.getElementById(anchor);
        if (element) {
          element.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);
    } else {
      const element = document.getElementById(anchor);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
    closeSidebar();
  };

  const navigateToPage = (path) => {
    closeSidebar();
    navigate(path);
  };

  // Prevent default behavior for all links
  const handleLinkPreventDefault = (e, callback) => {
    e.preventDefault();
    e.stopPropagation();
    if (callback) {
      callback();
    }
  };

  return (
    <>
      {/* Sidebar Overlay */}
      <div 
        className={`sidebar-overlay ${isSidebarOpen ? 'open' : ''}`}
        onClick={closeSidebar}
      />
      
      <div className='navbar'>
        <div className="align-left">
          {/* Sidebar */}
          <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
            <button className="menu-btn" onClick={toggleSidebar}>
              {isSidebarOpen ? '✕' : '☰'}
            </button>
            
            <div className="sidebar-content">
              <h3>OWASP Top 10</h3>
              <ul className="vulnerability-list">
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/broken-access-control')}
                  >
                    <span className="vuln-number">1</span>
                    Broken Access Control
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/cryptographic-failures')}
                  >
                    <span className="vuln-number">2</span>
                    Cryptographic Failures
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/injection')}
                  >
                    <span className="vuln-number">3</span>
                    Injection
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/insecure-design')}
                  >
                    <span className="vuln-number">4</span>
                    Insecure Design
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/security-misconfiguration')}
                  >
                    <span className="vuln-number">5</span>
                    Security Misconfiguration
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/vulnerable-components')}
                  >
                    <span className="vuln-number">6</span>
                    Vulnerable Components
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/authentication-failures')}
                  >
                    <span className="vuln-number">7</span>
                    Authentication Failures
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/software-data-integrity')}
                  >
                    <span className="vuln-number">8</span>
                    Software & Data Integrity
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/security-logging-failures')}
                  >
                    <span className="vuln-number">9</span>
                    Security Logging Failures
                  </button>
                </li>
                <li>
                  <button 
                    className="sidebar-link"
                    onClick={() => navigateToPage('/server-side-request-forgery')}
                  >
                    <span className="vuln-number">10</span>
                    Server-Side Request Forgery
                  </button>
                </li>
              </ul>
            </div>
          </div>
          
          {/* Logo */}
          <div className='logo'>
            <img src="/logo32.png" alt="Logo" />
          </div>
        </div>

        {/* Navigation Links */}
        <ul className='link'>
          <li>
            <button 
              className="nav-link-button"
              onClick={() => navigate('/')}
            >
              Home
            </button>
          </li>
          <li>
            <a 
              href="https://owasp.org/Top10/" 
              target="_blank" 
              rel="noopener noreferrer"
              onClick={(e) => handleLinkPreventDefault(e)}
            >
              Study Material
            </a>
          </li>
          <li>
            <button 
              className="nav-link-button"
              onClick={() => navigate('/about')}
            >
              About
            </button>
          </li>
          <li>
            <button 
              className="nav-link-button"
              onClick={() => handleLinkClick('contact')}
            >
              Contact Us
            </button>
          </li>
        </ul>
      </div>
    </>
  );
}
