import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../App.css';

export default function Navbar() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const closeSidebar = () => {
    setIsSidebarOpen(false);
  };

  const handleLinkClick = (anchor) => {
    // If we're not on the home page, navigate to home first
    if (window.location.pathname !== '/') {
      navigate('/');
      // Wait for navigation then scroll
      setTimeout(() => {
        const element = document.getElementById(anchor);
        if (element) {
          element.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);
    } else {
      const element = document.getElementById(anchor);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
    closeSidebar();
  };

  const navigateToPage = (path) => {
    closeSidebar();
    navigate(path);
  };

  return (
    <>
      {/* Sidebar Overlay */}
      <div 
        className={`sidebar-overlay ${isSidebarOpen ? 'open' : ''}`}
        onClick={closeSidebar}
      />
      
      <div className='navbar'>
        <div className="align-left">
          {/* Sidebar */}
          <div className={`sidebar ${isSidebarOpen ? 'open' : ''}`}>
            <button className="menu-btn" onClick={toggleSidebar}>
              {isSidebarOpen ? '✕' : '☰'}
            </button>
            
            <div className="sidebar-content">
              <h3>OWASP Top 10</h3>
              <ul className="vulnerability-list">
                <li>
                  <a 
                    href="/broken-access-control" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      navigateToPage('/broken-access-control');
                    }}
                  >
                    <span className="vuln-number">1</span>
                    Broken Access Control
                  </a>
                </li>
                <li>
                  <a 
                    href="#A02" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A02');
                    }}
                  >
                    <span className="vuln-number">2</span>
                    Cryptographic Failures
                  </a>
                </li>
                <li>
                  <a 
                    href="#A03" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A03');
                    }}
                  >
                    <span className="vuln-number">3</span>
                    Injection
                  </a>
                </li>
                <li>
                  <a 
                    href="#A04" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A04');
                    }}
                  >
                    <span className="vuln-number">4</span>
                    Insecure Design
                  </a>
                </li>
                <li>
                  <a 
                    href="#A05" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A05');
                    }}
                  >
                    <span className="vuln-number">5</span>
                    Security Misconfiguration
                  </a>
                </li>
                <li>
                  <a 
                    href="#A06" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A06');
                    }}
                  >
                    <span className="vuln-number">6</span>
                    Vulnerable Components
                  </a>
                </li>
                <li>
                  <a 
                    href="#A07" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A07');
                    }}
                  >
                    <span className="vuln-number">7</span>
                    Authentication Failures
                  </a>
                </li>
                <li>
                  <a 
                    href="#A08" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A08');
                    }}
                  >
                    <span className="vuln-number">8</span>
                    Software & Data Integrity
                  </a>
                </li>
                <li>
                  <a 
                    href="#A09" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A09');
                    }}
                  >
                    <span className="vuln-number">9</span>
                    Security Logging Failures
                  </a>
                </li>
                <li>
                  <a 
                    href="#A10" 
                    className="sidebar-link"
                    onClick={(e) => {
                      e.preventDefault();
                      handleLinkClick('A10');
                    }}
                  >
                    <span className="vuln-number">10</span>
                    Server-Side Request Forgery
                  </a>
                </li>
              </ul>
            </div>
          </div>
          
          {/* Logo */}
          <div className='logo'>
            <img src="/logo32.png" alt="Logo" />
          </div>
        </div>

        {/* Navigation Links */}
        <ul className='link'>
          <li>
            <a 
              href="/" 
              onClick={(e) => {
                e.preventDefault();
                navigate('/');
              }}
            >
              Home
            </a>
          </li>
          <li><a href="https://owasp.org/Top10/" target="_blank" rel="noopener noreferrer">Study Material</a></li>
          <li>
            <a 
              href="/about" 
              onClick={(e) => {
                e.preventDefault();
                navigate('/about');
              }}
            >
              About
            </a>
          </li>
          <li>
            <a 
              href="#contact" 
              onClick={(e) => {
                e.preventDefault();
                handleLinkClick('contact');
              }}
            >
              Contact Us
            </a>
          </li>
        </ul>
      </div>
    </>
  );
}
