import React, { useState } from 'react';
import { ChevronLeft, Zap } from 'lucide-react';

// Data Array - Using the local image paths as requested.
// NOTE: I've added a 'details' field for better content on the detail view.
const vulnerabilities = [
  {
    id: 1,
    name: "Broken Access Control",
    description: "Restrictions on what authenticated users are allowed to do are not properly enforced, allowing attackers to access unauthorized functionality or data.",
    link: "./broken-access-control",
    image: "./brokenaccesscontrol.png",
    details: "Broken Access Control is often due to poor authorization checks. Prevent this by using centralized access control, implementing domain-level security checks, and enforcing the principle of least privilege."
  },
  {
    id: 2,
    name: "Cryptographic Failures",
    description: "Failures related to cryptography which often lead to exposure of sensitive data like passwords, credit cards, and health records.",
    link: "./cryptographic-failures",
    image: "/cryptography.png",
    details: "Always use strong, modern, and high-entropy encryption standards. Never store plain text passwords; use slow hashing functions like bcrypt. Ensure TLS/SSL is used for data in transit."
  },
  {
    id: 3,
    name: "Injection",
    description: "Untrusted data is sent to an interpreter as part of a command or query, leading to data loss, corruption, or unauthorized access.",
    link: "./injection",
    image: "/injuction.png",
    details: "The most effective way to prevent injection (SQL, NoSQL, OS Command) is to use parameterized queries (prepared statements) and input validation for all user-supplied data."
  },
  {
    id: 4,
    name: "Insecure Design",
    description: "Missing or ineffective control design that fails to prevent security flaws before implementation.",
    link: "./insecure-design",
    image: "/insecuredesign.png",
    details: "Focus on threat modeling and defining secure design patterns before writing code. Separate user and data flows based on trust levels and enforce strict input/output contracts."
  },
  {
    id: 5,
    name: "Security Misconfiguration",
    description: "Insecure configurations in any part of the application stack, from network services to platform settings.",
    link: "./security-misconfiguration",
    image: "/securitymissconfiguration.png",
    details: "Always patch software promptly, disable unused services and features, and use automated tools to audit security configuration settings across all environments."
  },
  {
    id: 6,
    name: "Vulnerable Components",
    description: "Using components with known vulnerabilities that can be exploited to compromise applications.",
    link: "./vulnerable-components",
    image: "./vulnerablecomponent.png",
    details: "Regularly scan all dependencies using tools like npm audit or Snyk. Update or replace components with known Common Vulnerabilities and Exposures (CVEs) immediately."
  },
  {
    id: 7,
    name: "Authentication Failures",
    description: "Broken authentication mechanisms that allow credential stuffing, brute force, and session hijacking attacks.",
    link: "./authentication-failures",
    image: "./authunticationfailures.png",
    details: "Enforce Multi-Factor Authentication (MFA). Use strong, server-side generated session tokens and securely manage session timeout and renewal policies."
  },
  {
    id: 8,
    name: "Software & Data Integrity",
    description: "Failures related to software updates, critical data, and CI/CD pipelines without integrity verification.",
    link: "./software-data-integrity",
    image: "./softwaredataintegrity.png",
    details: "Ensure software and data integrity by using digital signatures or checksums to verify that updates or code changes have not been tampered with before deployment."
  },
  {
    id: 9,
    name: "Security Logging Failures",
    description: "Failures in logging, monitoring, and incident response capabilities that prevent threat detection.",
    link: "./security-logging-failures",
    image: "./securityloggingfailure.png",
    details: "Implement comprehensive logging for all authentication and authorization failures, input validation errors, and critical business transactions. Ensure logs are immutable and monitored in real-time."
  },
  {
    id: 10,
    name: "Server-Side Request Forgery",
    description: "Web applications that fetch remote resources without validating user-supplied URLs.",
    link: "./server-side-request-forgery",
    image: "/serversideforgery.png",
    details: "Mitigate SSRF by enforcing a strict allow-list (whitelist) of permitted hostnames and IP addresses. Disallow fetching local or internal network resources entirely."
  }
];

// --- Sub-Component for Detail View ---
const VulnerabilityDetails = ({ vuln, onBack }) => (
  <div className="p-4 md:p-8 max-w-4xl mx-auto bg-white rounded-xl shadow-2xl mt-8 mb-16">
    <button 
      onClick={onBack} 
      className="flex items-center text-indigo-600 font-semibold hover:text-indigo-800 transition duration-300 mb-6 px-4 py-2 rounded-full border border-indigo-200 hover:bg-indigo-50"
    >
      <ChevronLeft size={20} className="mr-1" />
      Back to OWASP List
    </button>

    <div className="flex items-center space-x-4 mb-4">
      <div className="flex items-center justify-center w-12 h-12 text-2xl font-bold text-white bg-red-600 rounded-full shadow-lg">
        {vuln.id}
      </div>
      <h2 className="text-3xl font-extrabold text-gray-900 leading-tight">{vuln.name}</h2>
    </div>

    {/* Using the user's local image path with robust fallback */}
    <img 
      src={vuln.image} 
      alt={vuln.name} 
      className="w-full h-48 object-cover rounded-lg mb-6 shadow-md" 
      onError={(e) => {
        // Fallback for local images that might not load in the current environment
        e.target.src = `https://placehold.co/800x200/ef4444/ffffff?text=${vuln.name.replace(/\s/g, '+')}`;
        e.target.className += " border border-red-300";
      }}
    />

    <p className="text-xl text-gray-700 mb-6 italic border-l-4 border-red-500 pl-4">
      {vuln.description}
    </p>

    <div className="bg-red-50 p-6 rounded-lg border border-red-200">
      <h3 className="text-2xl font-bold text-red-700 mb-3 flex items-center">
        <Zap size={24} className="mr-2" />
        Remediation Strategy
      </h3>
      <p className="text-gray-800 leading-relaxed">
        {vuln.details}
      </p>
    </div>
  </div>
);

// --- Sub-Component for Card View ---
const VulnerabilityCard = ({ vulnerability, onSelect }) => {
  return (
    <div className="bg-white rounded-xl shadow-lg hover:shadow-2xl transition duration-500 overflow-hidden border border-gray-100 flex flex-col transform hover:scale-[1.02] cursor-pointer" onClick={() => onSelect(vulnerability.id)}>

      {/* Card Image and Overlay */}
      <div className="relative h-40">
        {/* Using the user's local image path with robust fallback */}
        <img
          src={vulnerability.image}
          alt={vulnerability.name}
          className="w-full h-full object-cover"
          onError={(e) => {
            // Fallback to a placeholder if the local image path is wrong
            e.target.src = `https://placehold.co/400x160/2563eb/ffffff?text=OWASP+${vulnerability.id}`;
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent flex items-end p-4">
          <div className="flex items-center justify-center w-8 h-8 text-lg font-bold text-white bg-indigo-600 rounded-full shadow-xl">
            {vulnerability.id}
          </div>
        </div>
      </div>

      {/* Card Content */}
      <div className="p-5 flex flex-col flex-grow">
        <h3 className="text-xl font-bold text-gray-900 mb-2 line-clamp-2">
          {vulnerability.name}
        </h3>

        <p className="text-sm text-gray-600 mb-4 flex-grow line-clamp-3">
          {vulnerability.description}
        </p>

        {/* CRITICAL FIX: Changed <a> to a button/div handler, styled to look like a link */}
        <div
          onClick={(e) => { e.stopPropagation(); onSelect(vulnerability.id); }} // Stop propagation so click on button doesn't trigger card click twice
          className="mt-auto inline-flex items-center justify-center px-4 py-2 border border-transparent text-sm font-medium rounded-lg text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 transition duration-300 shadow-md w-full"
        >
          Learn More
          <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2}
              d="M14 5l7 7m0 0l-7 7m7-7H3" />
          </svg>
        </div>
      </div>
    </div>
  );
};

// --- Main App Component ---
const OWASPTop10 = () => {
  // State to track which vulnerability is currently selected for detail view
  const [selectedVulnId, setSelectedVulnId] = useState(null);

  // Find the selected vulnerability object
  const selectedVuln = vulnerabilities.find(v => v.id === selectedVulnId);

  // Function to handle the selection and fix the scroll
  const handleViewChange = (id) => {
    setSelectedVulnId(id);

    // CRITICAL FIX: Scroll to the very top (0, 0) when switching views
    // This ensures the new content always starts from the top.
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  // Function to return to the list view
  const handleBackToList = () => {
    setSelectedVulnId(null);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans p-4 sm:p-8">

      {/* Conditional Rendering: Show Details or List */}
      {selectedVuln ? (

        // VIEW 2: Detail View
        <VulnerabilityDetails 
          vuln={selectedVuln} 
          onBack={handleBackToList} 
        />

      ) : (

        // VIEW 1: List View
        <div className="max-w-7xl mx-auto">
          <header className="text-center py-10">
            <h1 className="text-4xl sm:text-5xl font-extrabold text-gray-900 mb-3">
              OWASP Top 10 Vulnerabilities
            </h1>
            <p className="text-lg text-gray-600 max-w-3xl mx-auto">
              The most critical web application security risks identified by the Open Web Application Security Project.
            </p>
          </header>

          <main className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-6 p-4">
            {vulnerabilities.map((vuln) => (
              <VulnerabilityCard 
                key={vuln.id} 
                vulnerability={vuln} 
                onSelect={handleViewChange} 
              />
            ))}
          </main>

          <footer className="text-center mt-12 py-6 text-gray-500 text-sm border-t border-gray-200">
            Security knowledge provided by the OWASP Foundation.
          </footer>
        </div>
      )}
    </div>
  );
};

export default OWASPTop10;
