import React from 'react';

const OWASPTop10 = () => {
  const vulnerabilities = [
    {
      id: 1,
      name: "Broken Access Control",
      description: "Restrictions on what authenticated users are allowed to do are not properly enforced.",
      link: "https://owasp.org/Top10/A01_2021-Broken_Access_Control/",
      icon: "üîê",
      color: "bg-red-500"
    },
    {
      id: 2,
      name: "Cryptographic Failures",
      description: "Failures related to cryptography which often lead to sensitive data exposure.",
      link: "https://owasp.org/Top10/A02_2021-Cryptographic_Failures/",
      icon: "üîí",
      color: "bg-orange-500"
    },
    {
      id: 3,
      name: "Injection",
      description: "Untrusted data is sent to an interpreter as part of a command or query.",
      link: "https://owasp.org/Top10/A03_2021-Injection/",
      icon: "üíâ",
      color: "bg-amber-500"
    },
    {
      id: 4,
      name: "Insecure Design",
      description: "Missing or ineffective control design that fails to prevent security flaws.",
      link: "https://owasp.org/Top10/A04_2021-Insecure_Design/",
      icon: "üìê",
      color: "bg-yellow-500"
    },
    {
      id: 5,
      name: "Security Misconfiguration",
      description: "Insecure configurations in any part of the application stack.",
      link: "https://owasp.org/Top10/A05_2021-Security_Misconfiguration/",
      icon: "‚öôÔ∏è",
      color: "bg-lime-500"
    },
    {
      id: 6,
      name: "Vulnerable Components",
      description: "Using components with known vulnerabilities that can be exploited.",
      link: "https://owasp.org/Top10/A06_2021-Vulnerable_and_Outdated_Components/",
      icon: "üì¶",
      color: "bg-green-500"
    },
    {
      id: 7,
      name: "Authentication Failures",
      description: "Broken authentication mechanisms that allow credential stuffing and brute force attacks.",
      link: "https://owasp.org/Top10/A07_2021-Identification_and_Authentication_Failures/",
      icon: "üë§",
      color: "bg-emerald-500"
    },
    {
      id: 8,
      name: "Software & Data Integrity",
      description: "Failures related to software updates, critical data, and CI/CD pipelines.",
      link: "https://owasp.org/Top10/A08_2021-Software_and_Data_Integrity_Failures/",
      icon: "üìä",
      color: "bg-cyan-500"
    },
    {
      id: 9,
      name: "Security Logging Failures",
      description: "Failures in logging, monitoring, and incident response capabilities.",
      link: "https://owasp.org/Top10/A09_2021-Security_Logging_and_Monitoring_Failures/",
      icon: "üìù",
      color: "bg-blue-500"
    },
    {
      id: 10,
      name: "Server-Side Request Forgery",
      description: "Web applications that fetch remote resources without validating user-supplied URLs.",
      link: "https://owasp.org/Top10/A10_2021-Server-Side_Request_Forgery_%28SSRF%29/",
      icon: "üîÑ",
      color: "bg-purple-500"
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            OWASP Top 10 Vulnerabilities
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            The most critical web application security risks identified by the Open Web Application Security Project
          </p>
        </div>

        {/* First Row - 5 cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6 mb-6">
          {vulnerabilities.slice(0, 5).map((vuln) => (
            <VulnerabilityCard key={vuln.id} vulnerability={vuln} />
          ))}
        </div>

        {/* Second Row - 5 cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6">
          {vulnerabilities.slice(5, 10).map((vuln) => (
            <VulnerabilityCard key={vuln.id} vulnerability={vuln} />
          ))}
        </div>
      </div>
    </div>
  );
};

const VulnerabilityCard = ({ vulnerability }) => {
  return (
    <div className="bg-white rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 overflow-hidden group">
      {/* Colored top section with icon */}
      <div className={`${vulnerability.color} p-6 text-white`}>
        <div className="flex items-center justify-between">
          <span className="text-3xl">{vulnerability.icon}</span>
          <span className="text-2xl font-bold opacity-80">#{vulnerability.id}</span>
        </div>
      </div>
      
      {/* Content section */}
      <div className="p-6">
        <h3 className="text-lg font-bold text-gray-900 mb-3 line-clamp-2">
          {vulnerability.name}
        </h3>
        
        <p className="text-gray-600 text-sm leading-relaxed mb-4 line-clamp-3">
          {vulnerability.description}
        </p>
        
        <a
          href={vulnerability.link}
          target="_blank"
          rel="noopener noreferrer"
          className="inline-flex items-center text-blue-600 hover:text-blue-800 font-medium text-sm transition-colors duration-200 group-hover:translate-x-1 transform transition-transform"
        >
          Learn More
          <svg className="w-4 h-4 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
          </svg>
        </a>
      </div>
    </div>
  );
};

export default OWASPTop10;
